mapper.select:
	SELECT 
		id,
		type,
		status,
		pay_type,
		pay_status,
		total_price,
		contact,
		contact_phone,
		customer_id,
		promotion_id,
		supplier_id,
		supplier_name,
		sales_id,
		sales_name,
		begin_time,
		end_time,
		cancel_reason,
		flag,
		memo,
		create_time,
		create_by,
		update_time,
		update_by
	FROM orders 
	
	 
    	AND supplier_id = #{supplierId,jdbcType=BIGINT}
     
	 
    	AND type = #{orderType,jdbcType=INTEGER}
     
     
    	AND status = #{orderStatus,jdbcType=INTEGER}
     
     
    	AND pay_type = #{payType,jdbcType=INTEGER}
     
     
    	AND pay_status = #{payStatus,jdbcType=INTEGER}
     
     
    	AND contact = #{contact,jdbcType=VARCHAR}
     
     
    	AND contactPhone = #{contactPhone,jdbcType=VARCHAR}
     
     
    	AND customer_id = #{customerId,jdbcType=BIGINT}
     
     
    	AND sales_id = #{salesId,jdbcType=BIGINT}
     
     
    	AND create_time > #{startDate,jdbcType=TIMESTAMP}
     
      
    	AND create_time < #{endDate,jdbcType=TIMESTAMP}
     
    
    ORDER BY id DESC
	LIMIT #{startNum, jdbcType=INTEGER} ,#{pageSize, jdbcType=INTEGER}

	SELECT 
		count(1) 
	FROM orders 
	
	 
    	AND supplier_id = #{supplierId,jdbcType=BIGINT}
     
	 
    	AND type = #{orderType,jdbcType=INTEGER}
     
     
    	AND status = #{orderStatus,jdbcType=INTEGER}
     
     
    	AND pay_type = #{payType,jdbcType=INTEGER}
     
     
    	AND pay_status = #{payStatus,jdbcType=INTEGER}
     
     
    	AND contact = #{contact,jdbcType=VARCHAR}
     
     
    	AND contactPhone = #{contactPhone,jdbcType=VARCHAR}
     
     
    	AND customer_id = #{customerId,jdbcType=BIGINT}
     
     
    	AND sales_id = #{salesId,jdbcType=BIGINT}
     
     
    	AND create_time > #{startDate,jdbcType=TIMESTAMP}
     
      
    	AND create_time < #{endDate,jdbcType=TIMESTAMP}

mapper.update:
update orders
set status = #{status,jdbcType=INTEGER},
  cancel_reason = #{cancelReason,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  update_by = #{updateBy,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

update orders
set status = #{status,jdbcType=INTEGER},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  update_by = #{updateBy,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

update orders
set sales_id = #{salesId,jdbcType=INTEGER},
  sales_name = #{salesName,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

update orders
set status = #{status,jdbcType=INTEGER},
  pay_status = #{payStatus,jdbcType=INTEGER},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  update_by = #{updateBy,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}
